import React, { useState, useRef } from "react";
import { Link } from "react-router-dom";
import useAuth from "../../auth/useAuth";

import "./Register.css";

import { ArrowForwardIos } from "@material-ui/icons";

function Register() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState(false);

  const error = useRef();

  const handleStart = () => {
    const isEmail =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/;

      console.log(email.current.value)

    if (!isEmail.test(String(email.current.value).toLowerCase())) {
      error.current.innerText = "Please, enter a valid email to continue";
      setErrors(true);
    }
    setEmail(email.current.value);
  };

  const { register } = useAuth();

  return (
    <section className="register">
      <div className="top">
        <div className="wrapper">
          <img className="logo" src="assets/images/netflix.svg" alt="" />
          <Link to="/login">
            <button className="signInButton">Sign In</button>
          </Link>
        </div>
      </div>
      <div className="container">
        <div className="wrapper-text">
          <h1>Unlimited movies, TV shows, and more.</h1>
          <p>Watch anywhere. Cancel anytime.</p>
          <p>
            Ready to watch? Enter your email to create or restar your membership
          </p>
        </div>
        {!email ? (
          <div className="input">
            <input type="email" placeholder="Email address" ref={email} />
            <button className="registerButton" onClick={handleStart}>
              Get Started <ArrowForwardIos className="icon" />
            </button>
            <span ref={error}></span>
          </div>
        ) : (
          <form className="input">
            <input
              type="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
            />
            <button
              className="registerButton"
              onClick={(e) => {
                e.preventDefault();
                register({ email, password });
              }}
            >
              Start Membership <ArrowForwardIos className="icon" />
            </button>
          </form>
        )}
      </div>
    </section>
  );
}

export default Register;
